    private static final String PHONE_NUMBER_CHARACTERS = "0123456789 /-";

    public Boolean isEmail(String value) {
        return emailPattern.matcher(value).matches();
    }

    public String cleanseEmail(String email) {
        String identifier = email.split("@")[0];
        String domain = email.split("@")[1];
        String trimmedIdentifier = identifier.replace(".", "");
        String cleansedIdentifier = trimmedIdentifier.split("\\+")[0];
        return cleansedIdentifier + "@" + domain;
    }

    public Boolean isPhoneNumber(String value) {
        if (!value.startsWith("+")) return false;
        for (String character: value.split("")) {
            if (!PHONE_NUMBER_CHARACTERS.contains(character)) return false;
        }
        return true;
    }

    public String cleansePhoneNumber(String phoneNumber) {
        StringBuilder builder = new StringBuilder();
        for (String character: phoneNumber.split("")) {
            if (character.matches("[0-9]")) builder.append(character);
        }
        return new String(builder);
    }

    public Boolean isUsername(String value) {
        return !isEmail(value) && !isPhoneNumber(value);
    }

